func getActiveSprint(sprints []database.Sprint) database.Sprint {

	var activeSprint database.Sprint
	for _, sprint := range sprints {
		if sprint.DetermineStatus() == database.StatusInProgress {
			activeSprint = sprint
		}
	}
	return activeSprint
}

